@dispatcher.message_handler()
async def scan(message: types.Message) -> None:
    contract_address = message.text
    if not Web3.isAddress(contract_address):
        return
    contract_address = Web3.toChecksumAddress(contract_address)
    if len(bsc.eth.getCode(contract_address)) < 1:
        return
    result = await scan_token(contract_address)
    await message.reply(f'{result}', parse_mode='Markdown')


async def scan_token(contract_address: str) -> str:
    result = ''
    print(f'Scanning BSC... {contract_address}')
    source_code_endpoint = f"{api['bsc-api']}?module=contract&action=getsourcecode&address={contract_address}&apikey={api['bsc-apikey']}"
    source_code = await call_api(source_code_endpoint)
    if 'Contract source code not verified' in json.dumps(source_code):
        return f'🚫 *BSC CONTRACT NOT VERIFIED!* 🚫'
    source_abi_endpoint = f"{api['bsc-api']}?module=contract&action=getabi&address={contract_address}&apikey={api['bsc-apikey']}"
    source_abi = await call_api(source_abi_endpoint)
    source_abi = json.loads(source_abi['result'])
    source_abi = json.dumps(source_abi)
    #
    contract = bsc.eth.contract(address=contract_address, abi=source_abi)
    #
    token_name = contract.functions.name().call()
    token_decimals = contract.functions.decimals().call()
    token_total_supply = contract.functions.totalSupply().call()
    token_burned = contract.functions.balanceOf('0x000000000000000000000000000000000000dEaD').call()
    #
    honeypot_is = await call_api(f'https://aywt3wreda.execute-api.eu-west-1.amazonaws.com/default/IsHoneypot?chain=bsc2&token={contract_address}')
    #
    pair_address = get_liquidity_pair(contract_address)
    (tokens, wbnb) = get_pair_reserves(pair_address)
    if tokens < 1:
        return f'🚫 *PAIR HAS NO LIQUIDITY!* 🚫'
    dollar_liquidity = float(get_wbnb_price()) * float(Web3.fromWei(wbnb, 'ether'))
    pair_tokens = str(tokens)[:-token_decimals]
    token_price = float(dollar_liquidity) / float(pair_tokens)
    dollar_liquidity = '{:,.2f}'.format(dollar_liquidity)
    total_supply_burn_excluded = int(str(token_total_supply)[:-token_decimals]) - int(str(token_burned)[:-token_decimals]) if token_burned > 0 else int(str(token_total_supply)[:-token_decimals])
    token_price = '{:.18f}'.format(token_price)
    market_cap = int(float(token_price) * float(total_supply_burn_excluded))
    market_cap = '{:,}'.format(market_cap)
    pair_tokens_percent = (float(tokens) / float(token_total_supply)) * 100
    pair_tokens_percent = '{:.2f}'.format(pair_tokens_percent)
    #
    result += f'🔶 *Binance Smart Chain | {token_name}* [ㅤ](https://www.linkpicture.com/q/utilityinu.png)\n\n'
    result += f'*CA:* `{contract_address}`\n'
    result += f'*Source Code:* ✅\n'
    result += f'*Total Supply:* `{str(token_total_supply)[:-token_decimals]}`'
    if token_burned > 0:
        burn_percent = float(token_burned) / float(token_total_supply) * 100
        burn_percent = '{:.2f}'.format(burn_percent)
        result += f'* | Burned:* `{burn_percent} %`\n'
    else:
        result += f'\n'
    result += f'*Market Cap:* `$ {market_cap}`\n'
    result += f'*Price:* `{token_price}`\n'
    result += f'*Honeypot:* {honeypot_is["IsHoneypot"]}\n'
    if honeypot_is['MaxTxAmount'] > 0:
        result += f"*Max Tx:* `{str(honeypot_is['MaxTxAmount'])[:-token_decimals]}` *|* `{'{:.2f}'.format(Web3.fromWei(honeypot_is['MaxTxAmountBNB'], 'ether'))}` *WBNB*\n"
    result += f"*Buy Tax:* `{honeypot_is['BuyTax']} %` *| Sell Tax:* `{honeypot_is['SellTax']} %`\n"
    result += f'-\n'
    result += f'*Lock:* {check_lp_lock(pair_address, contract_address)}\n'
    result += f'*Tokens:* `{pair_tokens_percent} %` *in PancakeSwap*\n'
    result += f"*WBNB:* `{'{:.2f}'.format(Web3.fromWei(wbnb, 'ether'))}`\n"
    result += f'*________________________________________________*\n'
    if contract_address != '0x57f800371E52F4A65d84e9DCf5E3ADF670D3fd34':
        warnings = check_for_warnings(source_code)
        if warnings != '':
            result += f'{warnings}'
    result += '\n\n'
    result += f'[⬆️ UINU Telegram](https://t.me/UtilityInu) *|* [🌐 UINU Website](http://utilityinu.com) *|* [📈 UINU Chart](https://poocoin.app/tokens/0x57f800371e52f4a65d84e9dcf5e3adf670d3fd34)\n'
    #
    return result

def check_for_warnings(contract_source_code) -> str:
    result = ''
    warnings = {
        'blacklist': 'Owner can blacklist any wallet!',
        'tradingopen': 'Owner can disable trading!',
        'allowedtransfer': 'Owner can block transfers from wallet (can block sell)!',
        'updateallowedtransfer': 'Owner can block transfers from wallet (can block sell)!',
        'multitransfer': 'Owner can send tokens to multiple addresses at once!',
        'setistxlimitexempt': 'Owner can exclude any wallet from tx limit!',
        'blacklist(address[]': 'Can Blacklist multiple addresses at once!',
        'buycooldownsettings': 'Can set cooldown interval without restrictions!',
        'bulkisblacklisted(address[] memory accounts': 'Can Blacklist multiple addresses at once!',
        'writebcList': 'Owner can blacklist any wallet!',
        'bulkexcludefee': 'Owner can exclude multiple wallets from fees at once!',
        'changetxlimit': 'Owner can set  Max Tx  to 0!',
        'excludefromfee(address': 'Owner can exclude any wallet from fee!'
    }
    for warning, message in warnings.items():
        if warning in str(json.dumps(contract_source_code)).lower():
            result += f'*» {message}* 🔴\n'
    return result

